import React from 'react';
import { Link, useLocation } from 'react-router-dom';

function Navbar() {
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path;
  };

  const linkStyle = {
    color: '#000',
    textDecoration: 'none',
    marginRight: '20px',
    fontSize: '16px',
    fontFamily: 'Proxima Nova Regular'
  };

  const activeLinkStyle = {
    ...linkStyle,
    color: '#C41230'
  };

  const navContainerStyle = {
    background: '#fff',
    borderBottom: '1px solid #eee',
    width: '100%',
    position: 'relative'
  };

  const logoContainerStyle = {
    position: 'absolute',
    left: '0',
    top: '0',
    padding: '20px',
    background: '#fff',
    display: 'flex',
    alignItems: 'center'
  };

  return (
    <>
      <nav style={navContainerStyle}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          position: 'relative',
          height: '90px'  // Fixed height to match logo
        }}>
          <div style={logoContainerStyle}>
            <Link to="/" style={{ display: 'block', lineHeight: 0 }}>
              <img
                src="/San-Diego-State-University-Logo-removebg-preview.png"
                alt="SDSU Logo"
                style={{ 
                  height: '50px',
                  objectFit: 'contain',
                  marginLeft: '0'
                }}
              />
            </Link>
          </div>
        </div>
      </nav>

      <div style={{
        background: '#C41230',
        padding: '40px 0',
        position: 'relative'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '0 20px',
          color: '#fff',
          textShadow: '2px 2px 4px rgba(0,0,0,0.5)'
        }}>
          <h1 style={{
            fontSize: '48px',
            marginBottom: '10px',
            fontWeight: 'bold',
            fontFamily: 'Proxima Nova Black'
          }}>
            Welcome to Student Success Portal
          </h1>
          <p style={{
            fontSize: '24px',
            fontFamily: 'Proxima Nova Regular'
          }}>
            Monitor student progress, identify risks, and drive positive outcomes
          </p>
        </div>
      </div>

      <div style={{
        background: '#fff',
        borderBottom: '1px solid #eee',
        marginBottom: '20px'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '0 20px'
        }}>
          <Link
            to="/"
            style={isActive('/') ? activeLinkStyle : linkStyle}
          >
            Home
          </Link>
          <Link
            to="/overview"
            style={isActive('/overview') ? activeLinkStyle : linkStyle}
          >
            Overview
          </Link>
          <Link
            to="/alerts"
            style={isActive('/alerts') ? activeLinkStyle : linkStyle}
          >
            Alerts
          </Link>
          <Link
            to="/settings"
            style={isActive('/settings') ? activeLinkStyle : linkStyle}
          >
            Settings
          </Link>
        </div>
      </div>
    </>
  );
}

export default Navbar;
