import React from 'react';
import { Link, useLocation } from 'react-router-dom';

function Navbar() {
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path;
  };

  const linkStyle = {
    color: '#000',
    textDecoration: 'none',
    marginRight: '20px',
    fontSize: '16px'
  };

  const activeLinkStyle = {
    ...linkStyle,
    color: '#C41230'
  };

  return (
    <>
      <nav style={{
        background: '#fff',
        padding: '20px 0',
        borderBottom: '1px solid #eee'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '0 20px'
        }}>
          <Link to="/" style={{ display: 'inline-block' }}>
            <img
              src="/San-Diego-State-University-Logo-removebg-preview.png"
              alt="SDSU Logo"
              style={{ height: '50px' }}
            />
          </Link>
        </div>
      </nav>

      <div style={{
        background: '#C41230',
        padding: '40px 0',
        position: 'relative'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '0 20px',
          color: '#fff',
          textShadow: '2px 2px 4px rgba(0,0,0,0.5)'
        }}>
          <h1 style={{
            fontSize: '48px',
            marginBottom: '10px',
            fontWeight: 'bold'
          }}>
            Welcome to Student Success Portal
          </h1>
          <p style={{
            fontSize: '24px'
          }}>
            Monitor student progress, identify risks, and drive positive outcomes
          </p>
        </div>
      </div>

      <div style={{
        background: '#fff',
        borderBottom: '1px solid #eee',
        marginBottom: '20px'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '0 20px'
        }}>
          <Link
            to="/"
            style={isActive('/') ? activeLinkStyle : linkStyle}
          >
            Home
          </Link>
          <Link
            to="/overview"
            style={isActive('/overview') ? activeLinkStyle : linkStyle}
          >
            Overview
          </Link>
          <Link
            to="/alerts"
            style={isActive('/alerts') ? activeLinkStyle : linkStyle}
          >
            Alerts
          </Link>
          <Link
            to="/settings"
            style={isActive('/settings') ? activeLinkStyle : linkStyle}
          >
            Settings
          </Link>
        </div>
      </div>
    </>
  );
}

export default Navbar;
