import React from 'react';
import { Link, useLocation } from 'react-router-dom';

function Navbar() {
    const location = useLocation();

    const isActive = (path) => {
        return location.pathname === path;
    };

    const linkStyle = {
        color: '#000',
        textDecoration: 'none',
        marginRight: '20px',
        fontSize: '16px',
        fontFamily: 'Proxima Nova Regular'
    };

    const activeLinkStyle = {
        ...linkStyle,
        color: '#C41230'
    };

    const logoContainerStyle = {
        position: 'absolute',
        top: 0,
        left: 0,
        zIndex: 1000,
        padding: '10px 20px',
        background: 'transparent'
    };

    const logoStyle = {
        height: '35px',  // Reduced height to match image
        width: 'auto',
        display: 'block',
        margin: 0,
        padding: 0,
        objectFit: 'contain',
        background: 'transparent',
        mixBlendMode: 'multiply'
    };

    const bannerStyle = {
        width: '100%',
        height: '55px',  // Reduced height to minimize white space
        position: 'relative',
        backgroundImage: 'url("/newsitebannerv6.png")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        marginBottom: '0'  // Remove bottom margin
    };

    return (
        <>
            <div style={bannerStyle}>
                <div style={logoContainerStyle}>
                    <Link to="/" style={{ display: 'block', lineHeight: 0 }}>
                        <img
                            src="/San-Diego-State-University-Logo-removebg-preview.png"
                            alt="SDSU Logo"
                            style={logoStyle}
                        />
                    </Link>
                </div>
            </div>

            <div style={{
                background: '#fff',
                borderBottom: '1px solid #eee',
                marginBottom: '20px'
            }}>
                <div style={{
                    maxWidth: '1200px',
                    margin: '0 auto',
                    padding: '0 20px'
                }}>
                    <Link
                        to="/"
                        style={isActive('/') ? activeLinkStyle : linkStyle}
                    >
                        Home
                    </Link>
                    <Link
                        to="/overview"
                        style={isActive('/overview') ? activeLinkStyle : linkStyle}
                    >
                        Overview
                    </Link>
                    <Link
                        to="/alerts"
                        style={isActive('/alerts') ? activeLinkStyle : linkStyle}
                    >
                        Alerts
                    </Link>
                    <Link
                        to="/settings"
                        style={isActive('/settings') ? activeLinkStyle : linkStyle}
                    >
                        Settings
                    </Link>
                </div>
            </div>

            <div style={{
                padding: '40px 0',
                position: 'relative',
                background: '#f5f5f5'  // Light gray background instead of red
            }}>
                <div style={{
                    maxWidth: '1200px',
                    margin: '0 auto',
                    padding: '0 20px',
                    color: '#333',  // Darker text for better contrast
                }}>
                    <h1 style={{
                        fontSize: '48px',
                        marginBottom: '10px',
                        fontWeight: 'bold',
                        fontFamily: 'Proxima Nova Black'
                    }}>
                        Welcome to Student Success Portal
                    </h1>
                    <p style={{
                        fontSize: '24px',
                        fontFamily: 'Proxima Nova Regular'
                    }}>
                        Monitor student progress, identify risks, and drive positive outcomes
                    </p>
                </div>
            </div>
        </>
    );
}

export default Navbar;
